import React, {useState} from 'react'
import styles from './Header.module.css'
import MainNavbar from './MainNavigation/MainNavbar'

export default function Header(props) {

    const[white, setWhite] = useState(false);
    const[hide, setHide] = useState(false);

    const changeWhite = () => {
        let newPosition = window.scrollY;
        if(newPosition >= 100){
            setWhite(true);
        } else{
            setWhite(false);
        }
    }
    window.addEventListener('scroll', changeWhite)

    window.onscroll = function(e){
        if (this.oldScroll > this.scrollY && this.scrollY >=1200){
            setHide(true);
            this.oldScroll=this.scrollY
        }
    }

    return (
        <header className={`${styles.transparentNavbar} ${white ? styles.whiteNavbar : ''} ${hide?styles.hide:''}`}>
            <MainNavbar whiteHeader={white ? "true":''}/>
        </header>
    )
}
