import React, {useState, useEffect} from 'react'
import styles from './Header.module.css'
import MainNavbar from './MainNavigation/MainNavbar'

export default function Header(props) {

    const[white, setWhite] = useState(false);
    const[hide, setHide] = useState(false);
    const[y,setY] = useState(window.scrollY);

    let oldPosition = 0;
    
    const scrollDetect = useCallback(
        (e) => {
            const window = e.current.Target;
            if (y > window.scrollY) {
                console.log('up');
            } else if (y < window.scrollY) {
                console.log('down');
            }
            setY(window.scrollY);
        },
        [y],
    );
        
        // let newPosition = window.pageYOffset;
        // if (newPosition-oldPosition > 0 && newPosition >= 1200){
        //     setHide(true);
        // }
        // else if (newPosition - oldPosition < 0) {
        //     setHide(false);
        // }
        // if(newPosition >= 100){
        //     setWhite(true);
        // } else{
        //     setWhite(false);
        // }
        
        // console.log('old = ', oldPosition);
        // console.log('new = ', newPosition);
        // oldPosition = newPosition;
    
    useEffect(() =>{
        setHide(window.scrollY);
        window.addEventListener('scroll', scrollDetect);
        return () => {
            window.removeEventListener('scroll', scrollDetect);
        };
    }, [scrollDetect]);
    

    return (
        <header className={`${styles.transparentNavbar} ${white ? styles.whiteNavbar : ''} ${hide?styles.hide:''}`}>
            <MainNavbar whiteHeader={white ? "true":''}/>
        </header>
    )
}
