import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Header from "./components/Layout/Header/Header";
import Footer from './components/Layout/Footer/Footer';
import About from "./Pages/About/About";
import Login from "./Pages/Account/Login";
import Register from "./Pages/Account/Register";
import LandingPage from './Pages/LandingPage/LandingPage';
import Home from "./Pages/Home/Home";
import Write from "./Pages/Write/Write";
import SinglePost from "./components/SinglePost/SinglePost";
import ScrollToTop from "./components/ScrollToTop/ScrollToTop";
import { useContext, useState } from "react";
import { Context } from "./context/Context";
import { useLocation } from 'react-router'

function App() {
  const {user} = useContext(Context);
  const [whiteheader,setWhiteHeader] = useState(false);
  const location = useLocation.pathname();

  // const setWhiteHeaderFunction = (childData) =>{
  //     setTimeout(()=>setWhiteHeader(childData))
  // }
  const handleHeader = () =>{
    console.log(location)
  }


  return (
    <Router>

      {/* {(location.pathname ==='/write')?setWhiteHeader(true):setWhiteHeader(false)} */}
      <ScrollToTop/>
      {/* <Header whiteHeader={whiteheader}/> */}
      {/* {useLocation()} */}
      <Switch>
        <Route path="/" exact={true}>
          <Home />
        </Route>
        <Route path="/photography">
          <LandingPage section='Photography' />
        </Route>
        <Route path="/explore">
          <LandingPage section='Explore'/>
        </Route>
        <Route path="/photographers">
          <LandingPage section='Photographers'/>
        </Route>
        <Route path="/challenge">
          <LandingPage section='Challenge'/>
        </Route>
        <Route path="/book">
          <LandingPage section='Book'/>
        </Route>
        <Route path="/music">
          <LandingPage section='Music'/>
        </Route>
        <Route path="/life">
          <LandingPage section='Life'/>
        </Route>
        <Route path="/about">
          <About />
        </Route>
        <Route path="/:LandingPage/:postId">
           <SinglePost />
        </Route>
        <Route path='/login'>
          {user ? <Home /> : <Login />}
        </Route>
        <Route path='/register'>
          <Register />
        </Route>
        <Route path="/write">
          {user ? <Write/> : <Login />}
        </Route>
      </Switch>
      <Footer/>
    </Router>
  )
}

export default App;
