import React, { useState } from 'react'
import styles from './ShareButton.module.css'


const ShareButton = (props) => {
    const [visible, setVisible] = useState(false);
    const changeVisible = () => {
        if(window.scrollY >= 720){
            setVisible(true);
        } else {
            setVisible(false);
        }
    }
    window.addEventListener('scroll', changeVisible)
    return (
        <div className={`${styles.shareButtonContainer} ${visible?styles.visible:''}`}>
            <button className={styles.fbButton} href="#">
                <i className="fab fa-facebook-f"></i>
            </button>
            <div className={styles.quotes}></div>
            <button className={styles.twButton} href="#">
                <i className="fab fa-twitter"></i>
            </button>
            <button className={styles.mailButton} href="#">
                <i className="fa fa-envelope"></i>
            </button>
        </div>
    )
}

export default ShareButton
